DROP TABLE IF EXISTS `store_categories`;
CREATE TABLE `store_categories` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(225) NOT NULL DEFAULT 'null',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `store_coupons`;
CREATE TABLE `store_coupons` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `code` varchar(225) NOT NULL,
  `procent` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `store_keys`;
CREATE TABLE `store_keys` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `key` varchar(225) DEFAULT NULL,
  `store` int(11) NOT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '0',
  `email` varchar(225) DEFAULT NULL,
  `date` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
  `coupon` varchar(225) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `store` (`store`),
  CONSTRAINT `store_keys_ibfk_1` FOREIGN KEY (`store`) REFERENCES `store` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `vk_id` int(11) NOT NULL,
  `name` varchar(225) DEFAULT NULL,
  `image` varchar(225) DEFAULT NULL,
  `admin` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


